actionConfiguration.body:
() => {
  let city_id = PayoutCampaignForm.formData.city_id;
  let vehicle_id = PayoutCampaignForm.formData.vehicle_id;
  let campaigns = PayoutCampaignForm.formData.campaigns;
  let message = "";
  let show_alert = false;
  if (city_id == "" || vehicle_id == "") {
    message = "Please Select City and Vehicle before selecting Fixed Percentage Campaign";
    show_alert = true;
  } else if (!campaigns.fixed_percent_commission.active && !campaigns.trip_fare.active && !campaigns.dryrun.active && !campaigns.trip_based_incentive.active && !campaigns.login_incentive.active) {
    message = "Please select at least one Campaign";
    show_alert = true;
  } else if (campaigns.fixed_percent_commission.active) {
    const not_eligible_city = ["Bangalore", "Mumbai"];
    const not_eligible_vehicle = ["3 Wheeler", "3 Wheeler Helper"];
    let city_name = CampaignValidation.cityName();
    let vehicle_name = CampaignValidation.vehicleName();
    if (not_eligible_city.includes(city_name) && not_eligible_vehicle.includes(vehicle_name)) {
      message = "For selected City and Vehicle, downgraded campaign is enabled. Kindly contact Development team to create Fixed Percentage Commission campaign.";
      show_alert = true;
    }
  } else if (campaigns.trip_based_incentive.active) {
    let days = 90;
    let campaign_end_date = new Date(PayoutCampaignForm.formData.campaign_end_date);
    let campaign_start_date = new Date(PayoutCampaignForm.formData.campaign_start_date);
    campaign_start_date.setDate(campaign_start_date.getDate() + days);
    if (campaign_end_date.getTime() > campaign_start_date.getTime()) {
      message = "You can't create a Trip Based Incentive Campaign with more than " + days + " days of period.";
      show_alert = true;
    }
  }
  if (show_alert) {
    storeValue("valid_fixed_campaign_msg", message);
    return false;
  } else {
    return true;
  }
}

