actionConfiguration.body:
(city_id) => {
		if(typeof(city_id) != "undefined" && city_id != ""){
			let city_data = GeoRegionsSelect.data;
			let city=  city_data.filter(function(city){
				return city.value == city_id
			})[0]
			return city.label;
		}
		else{
			return "";
		}
	}

