actionConfiguration.body:
(vehicle_id) => {
		if(typeof(vehicle_id) != "undefined" && vehicle_id != ""){
			let vehicle_data = VehicleSelectionList.data;
			let vehicle = vehicle_data.filter(function(vehicle){
				return vehicle.value == vehicle_id
			})[0];
			return vehicle.label;
		}
		else{
			return "";
		}
	}

