export default {
	getCampaignConfig: () => {
		let geo_regions = GeoRegion.data;
		let vehicles_data = VehicleTypeData.data;
		let payout_campaign = CampaignConfig.data;
		payout_campaign.map(function(config){
			 let geo_name = geo_regions.filter(d => d.id == config['geo_region_id'])
			 config['city'] =  typeof geo_name[0] != 'undefined' ? geo_name[0].name : "Not Defined";
			 let vehicle_type_arr = [];
			 if(config['vehicles'].length !=0){
				 vehicle_type_arr = config['vehicles'].map( (veh)=>vehicles_data.filter(d => d.id == veh)[0])
			 }

			 let driver_vehicle_type_arr = [];
			 if(config['driver_vehicles'] != null && config['driver_vehicles'].length !=0){
				 driver_vehicle_type_arr = config['driver_vehicles'].map( (veh)=>vehicles_data.filter(d => d.id == veh)[0])
			 }

			config['vehicles_name'] =  vehicle_type_arr.reduce( (result, element) => {
				if(typeof element != 'undefined')
						result.push(element.vehicle_type);
				return result;
			}, []);

			config['driver_vehicles_name'] =  driver_vehicle_type_arr.reduce( (result, element) => {
				if(typeof element != 'undefined')
						result.push(element.vehicle_type);
				return result;
			}, []);



			 return config;
		})

		return payout_campaign;


	},
	findPositions: (arr) => {
   const indicies = [];
		if(typeof arr == 'undefined' ||arr.length == 0){
			return [];
		}
   CampaignConfig.data.forEach((element, index) => {
      if(arr.includes(element.id)){
         indicies.push(index);
      }
   });
   return indicies;
	}

}
