actionConfiguration.body:
() =>{
		let raw_data = ExpireModalFunc.getCurrentRow();
		let data = {};
		data['campaigns'] = [{
        "campaign_type": "TRIP_BASED_INCENTIVE"
      } ];
		data['city_id'] = raw_data.geo_region_id;
		PayoutCampaignTable.selectedRow.vehicles[0]
		data['vehicle_id'] = raw_data.vehicles[0];
		data['payout_campaign_id'] = raw_data.payout_campaign_id;
		data['expiry_date'] = ExpireModalFunc.getTodayDate(); 

		storeValue("expire_trip_based_campaign",data);
		ExpireCampaignByIdApi.run(
    // On success
    ()=>{
      showAlert("Campaign  Expired successfully");
			CampaignConfig.run();
			closeModal('expire_modal');

    },
    // On error
    ()=>{
      showAlert("Something went wrong");
    });
	}

